 		<td>   			<select name="sidebar2_visibility">  				<option value=""></option>  				<option value="show" <?php echo get_option('sidebar2_visibility') == "show"? "selected" : ""; ?>>Show</option>  				<option value="hidden" <?php echo get_option('sidebar2_visibility') == "hidden"? "selected" : ""; ?>>Hidden</option>  			</select>  		</td>  	</tr>	<tr><td colspan="2" class="submit">		<input type="submit" name="Submit" value="Update Options" class="add:the-list:newmeta"/>  		<input type="hidden" name="action" value="update" />  		<input type="hidden" name="page_options" value="sidebar1_position,sidebar2_position,sidebar1_visibility,sidebar2_visibility" />	</td>	</tr></tbody>  </table>  </form>  </div>  <?php  }?>